import styled from 'styled-components';

export const Container = styled.div`
    background-color: blueviolet;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100vh;

    form{
        background-color: goldenrod;
        margin: 2rem;
        padding: 0.5rem 2rem;
        border-radius: 30px;
    }

    input{
        margin-top: 0.5rem;
    }

`
