import styled from 'styled-components';

export const Container = styled.div`
    display: flex;
    width: 100%;
    height: 100vh;
    background-color: blueviolet;
    justify-content: center;
    align-items: center;

    .Titulo{
        display: block;
        justify-self: center;
    }

    form{
        display: grid;
    }

    button{
        padding: 1;
    }

    a{
        font-size: larger;
        justify-content: center;
        text-decoration: none;
        color: black;
    }
`
