import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { convertCompilerOptionsFromJson } from 'typescript';
import { Cursos } from '../../Data/databse';

import { Container } from './style';
const cursos = Cursos;

const VerCursos: React.FC = () => {

    function stopAction(e: Event){
      e.preventDefault();
    }

    {/*function mostrarCursos(){
      const id = document.getElementById ('idDoCurso');
      const titulo = document.getElementById('nomeDoCurso');
      const descricao = document.getElementById('descricao');
      const imagem = document.getElementById('imagem');
      const nomeProfessor = document.getElementById('nomeProfessor');
      const listaDeAulas = document.getElementById('linkDasAulas');
    }*/}

    function buscarCurso(id: idb){
      const Id = document.getElementById('busca');
      Cursos.filter(curso => curso.id === Id?.innerText);
      console.log(Cursos);
    }


    const [ search, setSearch ] = useState(true);
    
    return (
      <Container>
          { search ? (
              <form>
              <h1>Ver Cursos</h1>
              <hr />
              <input placeholder='Digite o ID do curso' id='busca'></input>
              <button type='submit' onClick={ () => {setSearch(!search), buscarCurso('busca')}}>Buscar</button>
              <Link to='/consulta'>Voltar</Link>
          </form>
          ) : (
              <form>
                <h1>Ver Cursos</h1>
                <hr />
                

                <button onClick={ () => setSearch(!search)}>Voltar</button>
              </form>
          ) }
          
      </Container>
  );
}

export default VerCursos;
