import styled from 'styled-components';

export const Container = styled.div`
    display: flex;
    width: 100%;
    height: 100vh;
    background-color: blueviolet;
    justify-content: center;
    align-items: center;
    color: black;

    form{
        display: grid;
        background-color: goldenrod;
        padding: 2rem;
        border-radius: 50px;
    }

    input{
        border-radius: 30px;
        padding: 0.5rem 2rem;
        margin: 1rem;
    }

    button{
        border-radius: 30px;
        padding: 0.5rem 1rem;
        font-size: medium;
        height: 15mm;
        width: 169px;
    }


`
