import styled from 'styled-components';

export const Container = styled.div`
    display: flex;
    width: 100%;
    height: 100vh;
    background-color: blueviolet;
    justify-content: center;
    align-items: center;

    form{
        display: grid;
        justify-content: center;
        align-items: center;
    }

    a{
        justify-self: center;
        font-size: larger;
        color: w;
    }
`
